import React from "react";

const getTodayString = () => {
  const [month, day, year] = new Date()
    .toLocaleDateString("en-US")
    .split("/");
  return `${year}-${month.padStart(2, "0")}-${day.padStart(2, "0")}`;
};

export const AppointmentForm = ({
  name,
  setName,
  contacts,
  title,
  setTitle,
  contact,
  setContact,
  date,
  setDate,
  time,
  setTime,
  handleSubmit
}) => {

  return (
    <>
    <form onSubmit={handleSubmit}>
      <label>Name:</label>
      <input
      type="text"
      value={name}
      />
      <label>Date:</label>
      <input
      type="date"
      value={date}/>
      <label>Time:</label>
      <input
      type="time"
      value={time}/>
      <button>Submit</button>
    </form>
    </>
  );
};
